INSERT INTO rarytas_database.categories (id, name, description) VALUES (1, 'Przystawki', 'Rozpocznij swoją kulinarną podróż od pysznych przystawek, które skradną Twoje podniebienie. Wybierz spośród aromatycznych krewetek marynowanych w cytrynie, wyrafinowanego carpaccio z polędwicy wołowej lub chrupiących nachos z soczystym sosem serowym.');
INSERT INTO rarytas_database.categories (id, name, description) VALUES (2, 'Makarony', 'Ciesz się smakiem Włoch dzięki naszym wyśmienitym makaronom. Wybierz klasykę - spaghetti bolognese, uwielbiane przez wszystkich penne carbonara lub wykwintne tagliatelle z łososiem i szpinakiem.');
INSERT INTO rarytas_database.categories (id, name, description) VALUES (3, 'Sałatki', 'Zachwyć swoje zmysły zdrowymi i świeżymi sałatkami. Spróbuj wyjątkowej sałatki greckiej z serem feta, orzeźwiającej sałatki z grillowanym ananasem i kurczakiem lub bogatej w białko sałatki z quinoa i pieczonymi warzywami.');
INSERT INTO rarytas_database.categories (id, name, description) VALUES (4, 'Zupy', 'Rozgrzej się pysznymi zupami. Spróbuj gładkiego kremu z pomidorów, tradycyjnego rosołu z kury lub pysznego kremu z dyni z nutą cynamonu.');
INSERT INTO rarytas_database.categories (id, name, description) VALUES (5, 'Pizza', 'Zanurz się w prawdziwym smaku pizzy. Wybierz klasyczną margheritę, pikantną pepperoni, pyszną capricciosę lub egzotyczną hawajską.');
INSERT INTO rarytas_database.categories (id, name, description) VALUES (6, 'Wegetariańskie', 'Odkryj różnorodność wegetariańskich smaków. Skosztuj aromatycznego risotto z grzybami leśnymi, egzotycznego curry z ciecierzycą i szpinakiem lub chrupiącego falafela w pitce z hummusem.');
INSERT INTO rarytas_database.categories (id, name, description) VALUES (7, 'Dla dzieci', 'Zaspokój apetyt najmłodszych przyjemnymi dla podniebienia daniami. Wybierz mini hamburgery z frytkami, krówki z kurczaka w panierce z ketchupem lub słodkie kanapki z nutellą i bananem.');
INSERT INTO rarytas_database.categories (id, name, description) VALUES (8, 'Napoje', 'Uzupełnij swoje doznania smakowe pysznymi napojami. Odkryj orzeźwiającą lemoniadę cytrynową, owocowy koktajl truskawkowy, a dla dorosłych - piwo rzemieślnicze IPA.');
